import React from 'react'
import styled from 'styled-components';
import Pink from "../../assets/blobPink.png";
import White from "../../assets/blob white.png";
import Purple from "../../assets/blob purple.png";

const Herosection = styled.div`
    width: 100vw;
    height: 45vw;
    background-color: var(--black);
    display: flex;
    justify-content: center;
    position: relative;
`;
const Blobs = styled.div`
    width:100%;
    right: 0;
    position: absolute;
`;
const PinkBlob = styled.div`
    width: calc(15% + 15vw);
    position: absolute;
    right: 0;
    top: calc(5rem + 5vw);
    z-index: 6;
`;
const WhiteBlob = styled.div`
    width: calc(20% + 20vw);
    position: absolute;
    right: calc(3.5rem + 3.5vw);
    top: calc(2rem + 2vw);
    z-index: 5;
`;
const PurpleBlob = styled.div`
    width: calc(10% + 10vw);
    position: absolute;
    right:0;
`;

const HeroSection = () => {
    return <Herosection>
        <Blobs>
            <PinkBlob>
                <img src={Pink} alt="pink blob" width="400" height="400" />
            </PinkBlob>
            <WhiteBlob>
                <img src={White} alt="white blob" width="400" height="400" />
            </WhiteBlob>
            <PurpleBlob>
                <img src={Purple} alt="purple blob" width="400" height="400" />

            </PurpleBlob>
        </Blobs>
        <MainContent>
            <leftBlock>
                <Topic>
                    <Circle/><span>we build web</span>
                </Topic>
                <Title>Transforming your digital </Title>
            </leftBlock>
        </MainContent>
    </Herosection>
};
export default HeroSection;
